import React, { useState } from 'react';
import { StyleSheet, Text, View, Pressable, TextInput, ScrollView } from 'react-native';
import { router, useSearchParams } from 'expo-router';
import { GlassView } from 'expo-glass-effect';
import { useTheme } from '@react-navigation/native';

export default function VehicleRegistration() {
  const theme = useTheme();
  const { type } = useSearchParams();

  const [make, setMake] = useState('');
  const [model, setModel] = useState('');
  const [year, setYear] = useState('');
  const [vin, setVin] = useState('');
  const [licensePlate, setLicensePlate] = useState('');
  const [purchaseDate, setPurchaseDate] = useState('');
  const [purchasePrice, setPurchasePrice] = useState('');
  const [mileage, setMileage] = useState('');
  const [notes, setNotes] = useState('');

  const handleSubmit = () => {
    // TODO: Implement Supabase integration to store the vehicle data
    console.log('Submitting vehicle data:', {
      type,
      make,
      model,
      year,
      vin,
      licensePlate,
      purchaseDate,
      purchasePrice,
      mileage,
      notes,
    });
    router.back(); // Navigate back to the home screen after submission
  };

  return (
    <ScrollView style={[styles.container, { backgroundColor: theme.colors.background }]}>
      <Text style={[styles.title, { color: theme.colors.text }]}>Vehicle Registration</Text>
      <Text style={[styles.subtitle, { color: theme.colors.text }]}>
        {type === 'car' ? 'Car' : 'Motorcycle'} Details
      </Text>

      <TextInput
        style={[styles.input, { backgroundColor: theme.dark ? 'rgba(255,255,255,0.1)' : 'rgba(0,0,0,0.05)', color: theme.colors.text }]}
        placeholder="Make"
        placeholderTextColor={theme.dark ? '#98989D' : '#666'}
        value={make}
        onChangeText={setMake}
      />
      <TextInput
        style={[styles.input, { backgroundColor: theme.dark ? 'rgba(255,255,255,0.1)' : 'rgba(0,0,0,0.05)', color: theme.colors.text }]}
        placeholder="Model"
        placeholderTextColor={theme.dark ? '#98989D' : '#666'}
        value={model}
        onChangeText={setModel}
      />
      <TextInput
        style={[styles.input, { backgroundColor: theme.dark ? 'rgba(255,255,255,0.1)' : 'rgba(0,0,0,0.05)', color: theme.colors.text }]}
        placeholder="Year"
        placeholderTextColor={theme.dark ? '#98989D' : '#666'}
        value={year}
        onChangeText={setYear}
        keyboardType="numeric"
      />
      <TextInput
        style={[styles.input, { backgroundColor: theme.dark ? 'rgba(255,255,255,0.1)' : 'rgba(0,0,0,0.05)', color: theme.colors.text }]}
        placeholder="VIN"
        placeholderTextColor={theme.dark ? '#98989D' : '#666'}
        value={vin}
        onChangeText={setVin}
      />
      <TextInput
        style={[styles.input, { backgroundColor: theme.dark ? 'rgba(255,255,255,0.1)' : 'rgba(0,0,0,0.05)', color: theme.colors.text }]}
        placeholder="License Plate"
        placeholderTextColor={theme.dark ? '#98989D' : '#666'}
        value={licensePlate}
        onChangeText={setLicensePlate}
      />
      <TextInput
        style={[styles.input, { backgroundColor: theme.dark ? 'rgba(255,255,255,0.1)' : 'rgba(0,0,0,0.05)', color: theme.colors.text }]}
        placeholder="Purchase Date"
        placeholderTextColor={theme.dark ? '#98989D' : '#666'}
        value={purchaseDate}
        onChangeText={setPurchaseDate}
      />
      <TextInput
        style={[styles.input, { backgroundColor: theme.dark ? 'rgba(255,255,255,0.1)' : 'rgba(0,0,0,0.05)', color: theme.colors.text }]}
        placeholder="Purchase Price"
        placeholderTextColor={theme.dark ? '#98989D' : '#666'}
        value={purchasePrice}
        onChangeText={setPurchasePrice}
        keyboardType="numeric"
      />
      <TextInput
        style={[styles.input, { backgroundColor: theme.dark ? 'rgba(255,255,255,0.1)' : 'rgba(0,0,0,0.05)', color: theme.colors.text }]}
        placeholder="Mileage"
        placeholderTextColor={theme.dark ? '#98989D' : '#666'}
        value={mileage}
        onChangeText={setMileage}
        keyboardType="numeric"
      />
      <TextInput
        style={[styles.input, { backgroundColor: theme.dark ? 'rgba(255,255,255,0.1)' : 'rgba(0,0,0,0.05)', color: theme.colors.text }]}
        placeholder="Notes"
        placeholderTextColor={theme.dark ? '#98989D' : '#666'}
        value={notes}
        onChangeText={setNotes}
        multiline
      />

      <Pressable onPress={handleSubmit}>
        <GlassView style={styles.button} glassEffectStyle="clear">
          <Text style={[styles.buttonText, { color: theme.colors.primary }]}>Submit</Text>
        </GlassView>
      </Pressable>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 16,
    textAlign: 'center',
  },
  subtitle: {
    fontSize: 18,
    marginBottom: 24,
    textAlign: 'center',
  },
  input: {
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 8,
    padding: 12,
    marginBottom: 16,
  },
  button: {
    paddingHorizontal: 20,
    paddingVertical: 12,
    borderRadius: 8,
    alignSelf: 'center',
  },
  buttonText: {
    fontSize: 16,
    fontWeight: '600',
    textAlign: 'center',
  },
});
```

**Explanation:**

*   **`useSearchParams`:**  This hook from `expo-router` allows you to access the `type` parameter from the route.
*   **State Variables:**  Uses `useState` to manage the input values for each field in the form.
*   **`handleSubmit`:**  This function is called when the "Submit" button is pressed. It currently logs the data to the console.  **TODO: Implement Supabase integration to store the vehicle data.**
*   **Input Fields:**  Creates `TextInput` components for each field in the form.
*   **Styling:**  Added basic styling for the form elements.

